import React, { Children, useReducer } from 'react';
import TemplateContext from './templateContext';
import TemplateReducer from './templateReducer';

import {
    GET_COMPONENTS,
    GET_TEXTS,
    CHANGE_POSITION,
    CHANGE_POSITION_T,
    CHANGE_ROTATE,
    CHANGE_POSITION_A,
    SET_COMPONENTS,
    SET_TEXTS,
    GET_TEMPLATES,
    GET_TEMPLATE,
    POST_TEMPLATES,
    GET_TEMPLATE_ID,
    TEMPLATE_ACTUAL,
    FILTRAR_TEMPLATES,
    FILTRAR_COMPONENTES,
    CLOSE_TEMPLATE,
    FILTRAR_COMPONENTES_IEC,
    FILTRAR_COMPONENTES_ANSI,
    FILTRAR_COMPONENTES_UNIONES,
    FILTRAR_COMPONENTES_OTROS,
    POST_TEXT_TEMPLATES,
    SET_TRANSLATEX,
    GET_TRANSLATEX,
    SET_TRANSLATEY,
    GET_TRANSLATEY,
    SYNCRONIZE

} from '../../types/index'
import apiDiagrams from '../../config/axios';
import AsyncStorage from '@react-native-async-storage/async-storage';

const TemplateState = props => {

    const initialState = {
        texts: [],
        coordXY: [],
        coordXYT: [],
        coordLn: [],
        templates: [],
        template: null,
        templatesFound: [],
        IECcomponents: [],
        IECcomponentsFounds: [],
        ANSIcomponents: [],
        ANSIcomponentsFounds: [],
        UNIONEScomponents: [],
        UNIONEScomponentsFounds: [],
        OTROScomponents: [],
        OTROScomponentsFounds: [],
        translateX: 0,
        translateY: 0
    }

    const [state, dispatch] = useReducer(TemplateReducer, initialState);

    const getComponents = async () => {
        const res = await apiDiagrams.get('/components');
        dispatch({
            type: GET_COMPONENTS,
            payload: res.data.data
        })
    }

    const getTexts = async () => {
        const res = await apiDiagrams.get('/texts');
        dispatch({
            type: GET_TEXTS,
            payload: res.data.data
        })
    }

    const setcoordXY = (data) => {
        dispatch({
            type: CHANGE_POSITION,
            payload: data
        })
    }

    const setcoordXYT = (data) => {
        dispatch({
            type: CHANGE_POSITION_T,
            payload: data
        })
    }

    const setTranslateX = (data) => {
        dispatch({
            type: SET_TRANSLATEX,
            payload: data
        })
    }

    const getTranslateX = (data) => {
        dispatch({
            type: SET_TRANSLATEX,
            payload: data
        })
    }

    const setTranslateY = (data) => {
        dispatch({
            type: SET_TRANSLATEY,
            payload: data
        })
    }

    const getTranslateY = (data) => {
        dispatch({
            type: GET_TRANSLATEY,
            payload: data
        })
    }

    const setCoordLn = (data) => {
        dispatch({
            type: CHANGE_POSITION_A,
            payload: data
        })
    }

    const closeTemplate = () => {
        dispatch({
            type: CLOSE_TEMPLATE
        })
    }

    const setcomponents = (data) => {
        dispatch({
            type: SET_COMPONENTS,
            payload: data
        })
    }

    const setTexts = (data) => {
        dispatch({
            type: SET_TEXTS,
            payload: data
        })
    }

    const getTemplates = async () => {
        try {
            const res = await apiDiagrams.get('/templates');
            await AsyncStorage.setItem("templates", JSON.stringify(res.data.data));
            dispatch({
                type: GET_TEMPLATES,
                payload: res.data.data
            })
        } catch (error) {
            console.log(error)
        }
    }

    const getTemplate = async (id) => {
        try {
            const res = await apiDiagrams.get(`/templates/${id}`);

            dispatch({
                type: GET_TEMPLATE,
                payload: res.data,
            });
        } catch (error) {
            console.log(error);
        }
    }

    const setTemplates = async (data) => {
        try {
            const res = await apiDiagrams.post('/templates', data);
            dispatch({
                type: POST_TEMPLATES,
                payload: res.data.data
            })
        } catch (error) {
            console.error(error);
        }
    }

    const setTextTemplates = async (data) => {
        try {
            const res = await apiDiagrams.post('/templates', data);
            dispatch({
                type: POST_TEXT_TEMPLATES,
                payload: res.data.data
            })
        } catch (error) {
            console.error(error);
        }
    }

    const setTemplate = async (data, id) => {
        try {
            const res = await apiDiagrams.put(`/templates/${id}`, data)
            dispatch({
                type: TEMPLATE_ACTUAL,
                payload: res.data.data
            });
        } catch (error) {

        }
    }

    const setTemplateState = (data) => {
        dispatch({
            type: GET_TEMPLATE_ID,
            payload: data
        });
    }

    const searchTemplate = (data) => {
        dispatch({
            type: FILTRAR_TEMPLATES,
            payload: data
        })
    }

    const searchComponentIEC = (data) => {
        dispatch({
            type: FILTRAR_COMPONENTES_IEC,
            payload: data
        })
    }

    const searchComponentANSI = (data) => {
        dispatch({
            type: FILTRAR_COMPONENTES_ANSI,
            payload: data
        })
    }

    const searchComponentUNIONES = (data) => {
        dispatch({
            type: FILTRAR_COMPONENTES_UNIONES,
            payload: data
        })
    }

    const searchComponentOTROS = (data) => {
        dispatch({
            type: FILTRAR_COMPONENTES_OTROS,
            payload: data
        })
    }

    const syncronizedTemplates = async (data) => {
        try {
            const respuesta = await apiDiagrams.post(`/syncronize`, data);
            dispatch({
                type: SYNCRONIZE,
                payload: respuesta.data.templates
            })
        } catch (error) {
            
        }
    }

    return (
        <TemplateContext.Provider
            value={{
                texts: state.texts,
                coordXY: state.coordXY,
                coordXYT: state.coordXYT,
                coordLn: state.coordLn,
                templates: state.templates,
                template: state.template,
                templatesFound: state.templatesFound,
                IECcomponents: state.IECcomponents,
                IECcomponentsFounds: state.IECcomponentsFounds,
                ANSIcomponents: state.ANSIcomponents,
                ANSIcomponentsFounds: state.ANSIcomponentsFounds,
                UNIONEScomponents: state.UNIONEScomponents,
                UNIONEScomponentsFounds: state.UNIONEScomponentsFounds,
                OTROScomponents: state.OTROScomponents,
                OTROScomponentsFounds: state.OTROScomponentsFounds,
                translateX: state.translateX,
                translateY: state.translateY,
                setTranslateX,
                getTranslateX,
                setTranslateY,
                getTranslateY,
                setcomponents,
                getComponents,
                setTexts,
                getTexts,
                setcoordXY,
                setcoordXYT,
                setCoordLn,
                closeTemplate,
                getTemplates,
                getTemplate,
                setTemplate,
                setTemplateState,
                setTemplates,
                setTextTemplates,
                searchTemplate,
                searchComponentIEC,
                searchComponentANSI,
                searchComponentUNIONES,
                searchComponentOTROS
            }}
        >
            {props.children}
        </TemplateContext.Provider>
    );
}

export default TemplateState;
